import { Route, Routes } from "react-router-dom";
import "./App.css";

import TopNav from "./components/TopNav";
import SideNav from "./components/SideNav";
import Home from "./components/Home";
import Category from "./components/Category";
import Book from "./components/Book";
import UserInfo from "./components/UserInfo";
import Seller from "./components/Seller";
import VerifiedSeller from "./components/VerifiedSeller";
import Recommend from "./components/Recommend";
import About from "./components/About";
import Terms from "./components/Terms";
import Privacy from "./components/Privacy";
import Safety from "./components/Safety";
import AdminBooks from './components/AdminBooks';
import Search from "./components/Search";

import { useEffect, useState } from "react";
import { MongoClient } from 'mongodb';

const client = new MongoClient('your_mongodb_connection_string');
const dbName = 'your_database_name';

function App() {
  const [books, setBooks] = useState([]);
  const [userId, setUserId] = useState("");
  const [userPic, setUserPic] = useState("");
  const [wishlist, setWishlist] = useState([]);
  const [verified, setVerified] = useState(false);
  const [userData, setUserData] = useState({});

  useEffect(() => {
    const fetchBooks = async () => {
      await client.connect();
      const database = client.db(dbName);
      const booksCollection = database.collection('books');
      const booksData = await booksCollection.find({}).toArray();
      setBooks(booksData);
    };

    fetchBooks();

    const fetchUserData = async (user) => {
      const uId = user.email.split("@")[0];
      setUserId(uId);
      setUserPic(user.photoURL);

      const userDocRef = client.db(dbName).collection("users").findOne({ email: user.email });
      if (!userDocRef) {
        const newUser = {
          name: user.displayName,
          email: user.email,
          image: user.photoURL,
          wishlist: [],
          verified: false,
        };
        await client.db(dbName).collection("users").insertOne(newUser);
        setUserData(newUser);
      } else {
        const userData = await userDocRef;
        setUserData(userData);
        setWishlist(userData.wishlist || []);
        setVerified(userData.verified || false);
      }
    };

    // Simulating user authentication
    const user = { /* user object from your auth system */ };
    if (user) {
      fetchUserData(user);
    } else {
      setUserId("");
      setUserPic("");
      setWishlist([]);
      setVerified(false);
      setUserData({});
    }

    return () => {
      client.close();
    };
  }, []);

  return (
    <>
      <div id="navigation">
        <SideNav userId={userId} userPic={userPic} />
        <div id="mainContent">
          <TopNav />
          <Routes>
            <Route path="/" element={<Home books={books} />} />
            <Route path="category" element={<Category books={books} />} />
            <Route path="category/:catId" element={<Category books={books} />} />
            <Route path="book/:bookId" element={<Book books={books} wishlist={wishlist} setWishlist={setWishlist} user={userId} />} />
            <Route path="seller" element={<Seller setVerified={setVerified} verified={verified} user={userId} />} />
            <Route path="seller/verified" element={<VerifiedSeller verified={verified} userId={userId} user={userData} />} />
            <Route path="recommend" element={<Recommend />} />
            <Route path="about" element={<About />} />
            <Route path="about/terms&condition" element={<Terms />} />
            <Route path="about/privacy-policy" element={<Privacy />} />
            <Route path="about/safety-remarks" element={<Safety />} />
            <Route path="/admin/books" element={<AdminBooks />} />
            <Route path="user/:userId" element={<UserInfo books={books} currUser={userId} userData={userData} />} />
            <Route path="search/:option/:search" element={<Search books={books} />} />
          </Routes>
        </div>
      </div>
    </>
  );
}

export default App;
